def run():
    with open("rdoc.txt", "a+") as rdoc_file:
        for i in range(1, random.randint(10, 10000)):
            record = generate_record()
            json_object = json.dumps(record)
            rdoc_file.write(json_object)
            rdoc_file.write('\n')

def generate_record() -> dict:
    appn_num, appn_title = random.choice(list(appropriation_dict.items()))
    ba_number, ba_title = random.choice(list(budget_activity_dict.items()))
    title = ""
    for i in range(1, random.randint(1, 10)):
        title += ' '.join([random.choice(i) for i in l]) + ' '
    title = title.strip()
    pe_mission_description_and_budget_justification = ''
    for i in range(1, random.randint(5, 50)):
        pe_mission_description_and_budget_justification += ' '.join([random.choice(i) for i in l]) + ' '
    pe_mission_description_and_budget_justification = pe_mission_description_and_budget_justification.strip()
    record = {
        "Appn_Num": appn_num,
        "Appn_Title": appn_title,
        "BudgetCycle": "PB",
        "BudgetYear": "2022",
        "BA_Number": ba_number,
        "BA_Title": ba_title,
        "R1_Line_Num": id_generator(size=4),
        "PE_Title": title,
        "PE_Num": id_generator(size=7),
        "Organization": "Army",
        "OrganizationCode": "ARMY",
        "PE_MDAP_Code": None,
        "PE_MSN_Dsec": pe_mission_description_and_budget_justification,
        "PEFund_APY": round(random.uniform(1.33, 100.66), 2) if random.randint(1, 10) > 5 else None,
        "PEFund_CY": round(random.uniform(1.33, 100.66), 2) if random.randint(1, 10) > 5 else None,
        "PEFund_PY": round(random.uniform(1.33, 100.66), 2) if random.randint(1, 10) > 5 else None,
        "PEFund_BY1": round(random.uniform(1.33, 100.66), 2) if random.randint(1, 10) > 5 else None,
        "PEFund_BY1Base": round(random.uniform(1.33, 100.66), 2) if random.randint(1, 10) > 5 else None,
        "PEFund_BY1OCO": round(random.uniform(1.33, 100.66), 2) if random.randint(1, 10) > 5 else None,
        "PEFund_BY2": round(random.uniform(1.33, 100.66), 2) if random.randint(1, 10) > 5 else None,
        "PEFund_BY3": round(random.uniform(1.33, 100.66), 2) if random.randint(1, 10) > 5 else None,
        "PEFund_BY4": round(random.uniform(1.33, 100.66), 2) if random.randint(1, 10) > 5 else None,
        "PEFund_BY5": round(random.uniform(1.33, 100.66), 2) if random.randint(1, 10) > 5 else None,
        "PEFund_CTC": round(random.uniform(1.33, 100.66), 2) if random.randint(1, 10) > 5 else None,
        "PEFund_Total": round(random.uniform(1.33, 100.66), 2) if random.randint(1, 10) > 5 else None,
    }
    return record


def id_generator(size=6, chars=string.ascii_uppercase + string.digits):
    return ''.join(random.choice(chars) for _ in range(size))


if __name__ == '__main__':
    run()
